import React, { useState } from 'react';

// Funcția de login
async function loginUser(userName, email, password) {
  try {
    const response = await fetch('https://localhost:7157/api/Users/login', {  
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ userName, email, password })  // Trimiți UserName, Email și Password
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(errorText);
    }

    const data = await response.json();
    console.log('Login successful:', data);
    return data;

  } catch (err) {
    console.error('Login failed:', err.message);
    throw err;
  }
}
const handleLogin = () => {
  setIsLoggedIn(true);
};

const handleLogout = () => {
  setIsLoggedIn(false);
};

function LoginForm() {
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Apelăm funcția loginUser cu UserName, Email și Password
      const data = await loginUser(userName, email, password);
      setMessage(data.message || 'Login successful!');
      console.log('User:', data);

      // Exemplu: poți salva userId-ul sau redirecționa
      // localStorage.setItem('userId', data.userId);
      // navigate('/dashboard');

    } catch (error) {
      setMessage(`Login failed: ${error.message}`);
    }
  };

  return (
    <div className="login-container">
      <h1>TrustPay</h1>
      <h2>Enter your account</h2>
      <input type="text" placeholder="Username" />
      <input type="text" placeholder="Email" />
      <input type="password" placeholder="Password" />
      <button onClick={handleLogin}>Login</button>
    </div>
  );}

export default LoginForm;
