import React, { useEffect, useState } from 'react';
import './App.css';
import logo from './logo1.png';

// Stiluri inline pentru noul design
const styles = {
  chromeTabsContainer: {
    backgroundColor: '#f0f3f5',
    padding: '0 20px',
    borderBottom: '1px solid #e0e0e0',
    display: 'flex',
    overflowX: 'auto',
    whiteSpace: 'nowrap',
    marginBottom: '20px'
  },
  chromeTab: {
    background: 'transparent',
    border: 'none',
    borderRadius: '8px 8px 0 0',
    padding: '12px 20px',
    margin: '0 4px',
    fontSize: '15px',
    fontWeight: '500',
    color: '#5f6368',
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    position: 'relative',
    marginBottom: '-1px'
  },
  chromeTabActive: {
    background: '#fff',
    color: '#1a73e8',
    borderTop: '2px solid #1a73e8',
    boxShadow: '0 -2px 10px rgba(0,0,0,0.05)'
  },
  accountTab: {
    backgroundColor: '#fff',
    borderRadius: '12px',
    boxShadow: '0 2px 12px rgba(0,0,0,0.1)',
    padding: '24px',
    margin: '20px 0',
    display: 'flex',
    flexDirection: 'column',
    gap: '20px'
  },
  accountInfo: {
    borderBottom: '1px solid #eaeaea',
    paddingBottom: '16px'
  },
  accountTitle: {
    fontSize: '18px',
    margin: '0 0 8px 0',
    color: '#202124'
  },
  accountBalance: {
    fontSize: '24px',
    fontWeight: '500',
    color: '#202124',
    margin: '10px 0'
  },
  accountActions: {
    display: 'flex',
    gap: '20px',
    flexWrap: 'wrap'
  },
  actionButton: {
    padding: '12px 24px',
    borderRadius: '8px',
    border: 'none',
    fontSize: '15px',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'all 0.2s ease'
  },
  transferButton: {
    backgroundColor: '#1a73e8',
    color: 'white'
  },
  historyButton: {
    backgroundColor: '#f1f3f4',
    color: '#5f6368'
  }
};

function Dashboard({ user, onLogout }) {
  const [accounts, setAccounts] = useState([]);
  const [transactions, setTransactions] = useState({});
  const [currentTab, setCurrentTab] = useState(null);
  const [showTransferForm, setShowTransferForm] = useState(false);
  const [fromAccountId, setFromAccountId] = useState(null);
  const [toAccountId, setToAccountId] = useState('');
  const [transferAmount, setTransferAmount] = useState('');
  const [transferCurrency, setTransferCurrency] = useState('RON');
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const fetchAccounts = async () => {
    try {
      const response = await fetch(`https://localhost:7157/api/Accounts/user/${user.userId}`);
      const data = await response.json();
      if (Array.isArray(data) && data.length > 0) {
        setAccounts(data);
        setCurrentTab(data[0].accountType);
      } else {
        setAccounts([]);
      }
    } catch (error) {
      console.error('Error fetching accounts:', error);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, [user.userId]);



  const transferFunds = async () => {
    const parsedAmount = parseFloat(transferAmount);
    if (!parsedAmount || parsedAmount <= 0) {
      setMessageType('error');
      setMessage("Suma introdusă nu este validă.");
      return;
    }

    if (fromAccountId === toAccountId) {
      setMessageType('error');
      setMessage("Nu poți transfera către același cont.");
      return;
    }

    try {
      const response = await fetch('https://localhost:7157/api/Transactions/transfer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          fromAccountId,
          toAccountId,
          amount: parsedAmount,
          currency: transferCurrency,
          transactionType: "Transfer"
        }),
      });

      if (response.ok) {
        setMessageType('success');
        setMessage("Transfer realizat cu succes!");
        await fetchAccounts();
        setShowTransferForm(false);
        setTransferAmount('');
        setTransferCurrency('RON');
      } else {
        const errorData = await response.json();
        setMessageType('error');
        setMessage("Eroare la transfer: " + (errorData.message || "necunoscută"));
      }
    } catch (error) {
      setMessageType('error');
      setMessage("Eroare: " + error.message);
    }

    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 5000);
  };

  const fetchTransactions = async (accountId) => {
    try {
      const response = await fetch(`https://localhost:7157/api/Transactions/account/${accountId}`);
      if (!response.ok) {
        throw new Error("Nu s-au putut prelua tranzacțiile.");
      }

      const data = await response.json();
      setTransactions((prev) => ({ ...prev, [accountId]: data }));
    } catch (error) {
      console.error("Eroare la preluarea tranzacțiilor:", error);
      setMessageType("error");
      setMessage("Eroare la preluarea tranzacțiilor.");
      setTimeout(() => {
        setMessage("");
        setMessageType("");
      }, 4000);
    }
  };

  const viewTransactions = async (accountId) => {
    await fetchTransactions(accountId);
    const trans = transactions[accountId] || [];

    if (trans.length === 0) {
      setMessageType("info");
      setMessage("Nicio tranzacție înregistrată.");
      return;
    }

    const formatted = trans.map((t) =>
      `→ De la contul ${t.fromAccountId} către ${t.toAccountId} — ${t.amount} ${t.currency} (${new Date(t.transactionDate).toLocaleString()})`
    );

    alert("Istoric tranzacții:\n\n" + formatted.join("\n"));
  };

  const accountTypes = [...new Set(accounts.map((acc) => acc.accountType))];

  return (
    <div className="app-container">
      <header className="header">
        <div className="header-left">
          <img src={logo} alt="TrustPay Logo" className="header-logo" />
          <h1>TrustPay</h1>
        </div>
        <div className="header-right">
          <span className="username">Salut, {user.userName}!</span>
          <button onClick={onLogout}>Logout</button>
        </div>
      </header>

      <div className="tabs chrome-style">
        {accountTypes.map((type) => (
          <button
            key={type}
            onClick={() => setCurrentTab(type)}
            className={currentTab === type ? 'active' : ''}
          >
            {type}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {accounts
          .filter((acc) => acc.accountType === currentTab)
          .map((acc) => (
            <div key={acc.accountId} className="account-tab">
              <div className="account-info">
                <h3>{acc.accountType}</h3>
                <p className="balance">Balanță: <strong>{acc.balance} {acc.currency}</strong></p>
              </div>
              <div className="account-actions">
                <button className="action-button transfer" onClick={() => {
                  setFromAccountId(acc.accountId);
                  setShowTransferForm(true);
                }}>
                  Mutare fonduri
                </button>
                <button className="action-button history" onClick={() => viewTransactions(acc.accountId)}>
                  Istoric Tranzacții
                </button>
              </div>
            </div>
          ))}
      </div>



      {showTransferForm && (
        <div className="add-funds-form animated-form">
          <h3>Mutare fonduri</h3>
          <div>
            <label>
              Către cont:
              <select
                value={toAccountId}
                onChange={(e) => setToAccountId(e.target.value)}
                className="input-field"
              >
                <option value="">Selectează</option>
                {accounts
                  .filter((acc) => acc.accountId !== fromAccountId)
                  .map((acc) => (
                    <option key={acc.accountId} value={acc.accountId}>
                      {acc.accountType} ({acc.currency})
                    </option>
                  ))}
              </select>
            </label>
          </div>
          <div>
            <label>
              Suma:
              <input
                type="number"
                value={transferAmount}
                onChange={(e) => setTransferAmount(e.target.value)}
                placeholder="Introdu suma"
                className="input-field"
              />
            </label>
          </div>
          <div>
            <label>
              Valuta:
              <select
                value={transferCurrency}
                onChange={(e) => setTransferCurrency(e.target.value)}
                className="input-field"
              >
                <option value="RON">RON</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </label>
          </div>
          <div className="add-funds-buttons">
            <button className="btn-add" onClick={transferFunds}>Transferă</button>
            <button className="btn-cancel" onClick={() => setShowTransferForm(false)}>Anulează</button>
          </div>

          {message && (
            <div className={`message ${messageType}`}>
              {message}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
