import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const Profile = ({ user }) => {
  const [profileData, setProfileData] = useState(null);

 useEffect(() => {
  if (user?.userName) {
    axios
      .get(`https://localhost:7157/api/Users/user/by-name/${user.userName}`)
      .then(res => setProfileData(res.data))
      .catch(err => {
        console.error('Eroare API:', err);  // ✅ vezi eroarea în consolă
        alert('Nu s-au putut încărca datele profilului.');
      });
  }
}, [user]);

  if (!profileData) return <p>Loading profile...</p>;

  return (
    <div>
      <h2>Profil Utilizator</h2>
      <p><strong>Nume:</strong> {profileData.userName}</p>
      <p><strong>ID utilizator:</strong> {profileData.userId}</p>
      <p><strong>Cont principal:</strong> {profileData.accountId}</p>
      <p><strong>Tip cont:</strong> {profileData.accountType}</p>
      <p><strong>Monedă:</strong> {profileData.currency}</p>
    </div>
  );
};

export default Profile;
