import React, { useEffect, useState } from 'react';
import './App.css';

function Dashboard({ user, onLogout }) {
  const [accounts, setAccounts] = useState([]);
  const [transactions, setTransactions] = useState({});
  const [currentTab, setCurrentTab] = useState(null);
  const [showAddFunds, setShowAddFunds] = useState(false); // Controlăm vizibilitatea formularului
  const [selectedAccountId, setSelectedAccountId] = useState(null); // Contul selectat pentru adăugare
  const [amount, setAmount] = useState('');
  const [currency, setCurrency] = useState('RON');

  useEffect(() => {
    async function fetchAccounts() {
      try {
        const response = await fetch(`https://localhost:7157/api/Accounts/user/${user.userId}`);
        const data = await response.json();
        if (Array.isArray(data) && data.length > 0) {
          setAccounts(data);
          setCurrentTab(data[0].accountType);
        } else {
          setAccounts([]);
        }
      } catch (error) {
        console.error('Error fetching accounts:', error);
      }
    }

    fetchAccounts();
  }, [user.userId]);

  const addFunds = async () => {
    const parsedAmount = parseFloat(amount);
    if (!parsedAmount || parsedAmount <= 0) {
      alert("Introduceți o sumă validă.");
      return;
    }
  
    try {
      const response = await fetch('https://localhost:7157/api/Transactions/addFunds', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fromAccountId: selectedAccountId,
          toAccountId: selectedAccountId,
          amount: parsedAmount,
          currency: currency,
          transactionType: "Deposit"
        }),
      });
  
      if (response.ok) {
        alert('Fondurile au fost adăugate cu succes!');
        setShowAddFunds(false);
        setAmount('');
        setCurrency('RON');
      } else {
        const error = await response.json();
        alert('Eroare la adăugare: ' + (error.message || JSON.stringify(error)));
      }
    } catch (error) {
      alert('Eroare de rețea: ' + error.message);
    }
  };
  
  
  
  

  const viewTransactions = (accountId) => {
    const trans = transactions[accountId] || [];
    if (trans.length === 0) {
      alert('Nicio tranzacție înregistrată.');
      return;
    }
    alert(
      'Istoricul tranzacțiilor:\n' +
        trans.map((t) => `${t.type} - ${t.amount} ${t.currency}`).join('\n')
    );
  };

  const accountTypes = [...new Set(accounts.map((acc) => acc.accountType))];

  return (
    <div className="app-container">
      <header className="header">
        <h1>TrustPay</h1>
        <div className="header-right">
          <span className="username">Salut, {user.userName}</span>
          <button onClick={onLogout}>Logout</button>
        </div>
      </header>

      <div className="tabs">
        {accountTypes.map((type) => (
          <button
            key={type}
            onClick={() => setCurrentTab(type)}
            className={currentTab === type ? 'active' : ''}
          >
            {type}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {accounts
          .filter((acc) => acc.accountType === currentTab)
          .map((acc) => (
            <div key={acc.accountId} className="account-tab">
              <h3>{acc.accountType}</h3>
              <p>Balanță: {acc.balance} {acc.currency}</p>
              <button
                onClick={() => {
                  setSelectedAccountId(acc.accountId);
                  setShowAddFunds(true);
                }}
              >
                Adaugă Bani
              </button>
              <button onClick={() => viewTransactions(acc.accountId)}>
                Istoric Tranzacții
              </button>
            </div>
          ))}
      </div>

      {/* Formularul pentru adăugare fonduri */}
      {showAddFunds && (
        <div className="add-funds-form">
          <h3>Adăugare Fonduri</h3>
          <div>
            <label>
              Suma:
              <input
                type="number"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Introdu suma"
              />
            </label>
          </div>
          <div>
            <label>
              Valuta:
              <select
                value={currency}
                onChange={(e) => setCurrency(e.target.value)}
              >
                <option value="RON">RON</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </label>
          </div>
          <button onClick={addFunds}>Adaugă</button>
          <button onClick={() => setShowAddFunds(false)}>Anulează</button>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
