import React, { useState } from 'react';
import './App.css';

function Dashboard({ userName = 'Utilizator' }) {
  const [accounts, setAccounts] = useState([
    { type: 'Cont Curent', balance: 0, transactions: [] },
  ]);
  const [selectedTab, setSelectedTab] = useState('Cont Curent');

  const handleCreateAccount = () => {
    const newType = prompt('Introduceți tipul contului (ex: Economii, Investiții):');
    if (!newType) return;

    const alreadyExists = accounts.some(acc => acc.type.toLowerCase() === newType.toLowerCase());
    if (alreadyExists) {
      alert('Contul deja există!');
      return;
    }

    setAccounts([...accounts, { type: newType, balance: 0, transactions: [] }]);
    setSelectedTab(newType);
  };

  const addFunds = () => {
    const amount = parseFloat(prompt('Introduceți suma de adăugat:'));
    if (isNaN(amount) || amount <= 0) return;

    setAccounts(prev =>
      prev.map(acc =>
        acc.type === selectedTab
          ? {
              ...acc,
              balance: acc.balance + amount,
              transactions: [...acc.transactions, { type: 'Depunere', amount }],
            }
          : acc
      )
    );
  };

  const viewTransactions = () => {
    const account = accounts.find(acc => acc.type === selectedTab);
    if (!account) return;
    alert(
      `Istoric pentru ${account.type}: ` +
        (account.transactions.length
          ? account.transactions.map(t => `${t.type} - ${t.amount} RON`).join(', ')
          : 'Fără tranzacții.')
    );
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>TrustPay</h1>
        <div className="header-right">
          <span className="username">Conectat ca: <strong>{userName}</strong></span>
          <button onClick={handleCreateAccount}>Creare cont</button>
          <button onClick={() => alert('Logout action')}>Logout</button>
        </div>
      </header>

      <div className="tabs">
        {accounts.map((acc, index) => (
          <button
            key={index}
            onClick={() => setSelectedTab(acc.type)}
            className={selectedTab === acc.type ? 'active' : ''}
          >
            {acc.type}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {accounts
          .filter(acc => acc.type === selectedTab)
          .map((acc, index) => (
            <div key={index}>
              <h3>{acc.type}</h3>
              <p>Balanta: {acc.balance} RON</p>
              <button onClick={addFunds}>Adaugă Bani</button>
              <button onClick={viewTransactions}>Istoric Tranzacții</button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Dashboard;
