import React, { useState } from 'react';

function Dashboard() {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);

  const addFunds = () => {
    const amount = parseFloat(prompt("Introduceți suma de adăugat:"));
    if (amount && amount > 0) {
      setBalance(balance + amount);
      setTransactions([...transactions, { type: 'Depunere', amount }]);
    }
  };

  const viewTransactions = () => {
    alert("Istoricul tranzacțiilor: " + transactions.map(t => `${t.type} - ${t.amount} RON`).join(', '));
  };

  return (
    <div className="dashboard">
      <h1>Contul tău</h1>
      <p>Balanță curentă: {balance} RON</p>
      <button onClick={addFunds}>Adaugă fonduri</button>
      <button onClick={viewTransactions}>Vezi tranzacții</button>
    </div>
  );
}

export default Dashboard;
