// App.js (Confirmare - nu necesită modificări noi față de ultima versiune)
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Dashboard from "./Dashboard";
import TransactionHistoryPage from "./TransactionHistoryPage";
import Profile from "./Profile";
import LoginForm from "./LoginForm";

function App() {
  const [user, setUser] = useState(null);

  const fetchAndSetUser = async (username) => {
    try {
      const response = await fetch(`https://localhost:7157/api/Users/user/by-name/${username}`);
      if (response.ok) {
        const userData = await response.json();
        setUser(userData); // Aceasta creează o NOUĂ REFERINȚĂ la obiectul user
        localStorage.setItem('user', JSON.stringify(userData));
        return userData;
      } else {
        console.error("Failed to fetch user profile:", response.statusText);
        handleLogout();
        return null;
      }
    } catch (error) {
      console.error("Error fetching user profile:", error);
      handleLogout();
      return null;
    }
  };

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        if (parsedUser.userName) {
          fetchAndSetUser(parsedUser.userName);
        } else {
          handleLogout();
        }
      } catch (e) {
        console.error("Failed to parse user from localStorage", e);
        handleLogout();
      }
    }
  }, []);

  const handleLogin = async (loginData) => {
    try {
      const response = await fetch('https://localhost:7157/api/Users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });

      if (response.ok) {
        const userData = await response.json();
        await fetchAndSetUser(userData.userName);
      } else {
        const errorData = await response.json();
        alert(errorData.message);
      }
    } catch (error) {
      console.error('Eroare la login:', error);
      alert('A apărut o eroare la conectare. Te rugăm să încerci din nou.');
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const handleUserUpdate = async () => {
    if (user?.userName) {
      // Această funcție va declanșa re-fetch-ul și actualizarea stării 'user' în App.js,
      // ceea ce va re-rendera Dashboard cu noul nume.
      await fetchAndSetUser(user.userName);
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={
            user ? <Navigate to="/" /> : <LoginForm onLogin={handleLogin} />
          }
        />
        <Route
          path="/"
          element={
            user ? (
              <Dashboard user={user} onLogout={handleLogout} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/istoric-tranzactii"
          element={
            user ? (
              <TransactionHistoryPage user={user} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/profile"
          element={
            user ? (
              <Profile user={user} onUserUpdate={handleUserUpdate} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="*" element={<Navigate to={user ? "/" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
