import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';
import logo from 'logo1.jpeg'; // asigură-te că calea e corectă

function LoginForm({ onLogin }) {
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://localhost:7157/api/Users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userName, email, password }),
      });

      const data = await response.json();
      if (response.ok && data.message === "Login successful") {
        setMessage(data.message);
        onLogin({ userId: data.userId, userName: data.userName });
        navigate('/dashboard');
      } else {
        setMessage("Login failed: " + data.message);
      }
    } catch (error) {
      setMessage("Login failed: " + error.message);
    }
  };

  return (
    <div className="login-page">
      <div className="login-logo-side">
        <img src={logo} alt="TrustPay Logo" className="login-logo" />
      </div>
      <div className="login-form-side">
        <h1>TrustPay</h1>
        <h2>Enter your account</h2>
        <form onSubmit={handleSubmit} className="login-form">
          <input
            type="text"
            placeholder="Username"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit">Login</button>
        </form>
        {message && <p style={{ marginTop: '10px', color: 'white' }}>{message}</p>}
      </div>
    </div>
  );
}

export default LoginForm;
