import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css';
import './App.css';

const Profile = ({ user }) => {
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    if (user?.userName) {
      axios
        .get(`http://localhost:5000/api/Users/user/by-name/${user.userName}`)
        .then(res => setProfileData(res.data))
        .catch(err => console.error(err));
    }
  }, [user]);

  if (!profileData) return <p>Loading profile...</p>;

  return (
    <div>
      <h2>Profil Utilizator</h2>
      <p><strong>Nume:</strong> {profileData.userName}</p>
      <p><strong>ID utilizator:</strong> {profileData.userId}</p>
      <p><strong>Cont principal:</strong> {profileData.accountId}</p>
      <p><strong>Tip cont:</strong> {profileData.accountType}</p>
      <p><strong>MonedÄƒ:</strong> {profileData.currency}</p>
    </div>
  );
};

export default Profile;
