import React, { useState, useEffect } from 'react';
import './App.css';

function Dashboard({ userId, userName, accounts }) {
  const [selectedTab, setSelectedTab] = useState('');

  // Selectează contul implicit
  useEffect(() => {
    if (accounts.length > 0) {
      setSelectedTab(accounts[0].accountType);
    }
  }, [accounts]);

  const addBankAccount = async () => {
    const accountType = prompt('Introduceți tipul noului cont bancar (ex: Economii, Investiții):');
    if (!accountType) return;

    const newAccount = {
      userId: userId,
      accountType: accountType,
      balance: 0,
      currency: 'RON',
      createdAt: new Date(),
    };

    const response = await fetch('/api/accounts', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newAccount),
    });

    if (response.ok) {
      const addedAccount = await response.json();
      accounts.push(addedAccount); // Adăugăm noul cont
      setSelectedTab(addedAccount.accountType); // Selectăm automat contul adăugat
    } else {
      alert('Eroare la adăugarea contului!');
    }
  };

  const addFunds = async () => {
    const amount = parseFloat(prompt('Introduceți suma de adăugat:'));
    if (isNaN(amount) || amount <= 0) return;

    const updatedAccounts = accounts.map((acc) =>
      acc.accountType === selectedTab
        ? {
            ...acc,
            balance: acc.balance + amount,
          }
        : acc
    );

    // Actualizăm starea pentru a reflecta noile fonduri
    setSelectedTab(updatedAccounts);
  };

  const viewTransactions = async () => {
    const account = accounts.find(acc => acc.accountType === selectedTab);
    if (!account) return;

    alert(`Tranzacții pentru contul ${account.accountType}: ` +
      (account.transactions.length ? account.transactions.map(t => `${t.transactionType} - ${t.amount} RON`).join(', ') : 'Nu există tranzacții.'));
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>TrustPay</h1>
        <div className="header-right">
          <span className="username">Conectat ca: <strong>{userName}</strong></span>
          <button onClick={addBankAccount}>Creare cont bancar</button>
          <button onClick={() => alert('Logout action')}>Logout</button>
        </div>
      </header>

      <div className="tabs">
        {accounts.map((acc, index) => (
          <button
            key={index}
            onClick={() => setSelectedTab(acc.accountType)}
            className={selectedTab === acc.accountType ? 'active' : ''}
          >
            {acc.accountType}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {accounts
          .filter(acc => acc.accountType === selectedTab)
          .map((acc, index) => (
            <div key={index}>
              <h3>{acc.accountType}</h3>
              <p>Balanta: {acc.balance} RON</p>
              <button onClick={addFunds}>Adaugă Bani</button>
              <button onClick={viewTransactions}>Istoric Tranzacții</button>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Dashboard;
