import React, { useState } from 'react';
import './App.css';
import logo from './logo.svg'; // Asigură-te că ai un logo sau înlocuiește cu ceva propriu

function App() {
  const [balance, setBalance] = useState(0); // Stocăm suma disponibilă
  const [transactions, setTransactions] = useState([]); // Stocăm tranzacțiile

  // Adaugă fonduri
  const addFunds = () => {
    const amount = parseFloat(prompt("Introduceți suma de adăugat:"));
    if (amount && amount > 0) {
      setBalance(balance + amount);
      setTransactions([...transactions, { type: "Add", amount }]);
    }
  };

  // Scade fonduri (pentru plăți)
  const makePayment = () => {
    const amount = parseFloat(prompt("Introduceți suma de plată:"));
    if (amount && amount > 0 && amount <= balance) {
      setBalance(balance - amount);
      setTransactions([...transactions, { type: "Payment", amount }]);
    } else {
      alert("Fonduri insuficiente sau sumă invalidă!");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Revolut Pay</h1>

        <div className="balance">
          <h2>Balanta: {balance} RON</h2>
        </div>

        <div className="buttons">
          <button onClick={addFunds}>Adaugă Fonduri</button>
          <button onClick={makePayment}>Plătește</button>
        </div>

        <h3>Tranzacții:</h3>
        <ul>
          {transactions.map((transaction, index) => (
            <li key={index}>
              {transaction.type}: {transaction.amount} RON
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
