import React, { useState } from 'react';

// Funcția de login
async function loginUser(userName, email, password) {
  try {
    const response = await fetch('https://localhost:7157/api/Users/login', {  
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ userName, email, password })  // Trimiți UserName, Email și Password
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(errorText);
    }

    const data = await response.json();
    console.log('Login successful:', data);
    return data;

  } catch (err) {
    console.error('Login failed:', err.message);
    throw err;
  }
}

function LoginForm() {
  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Apelăm funcția loginUser cu UserName, Email și Password
      const data = await loginUser(userName, email, password);
      setMessage(data.message || 'Login successful!');
      console.log('User:', data);

      // Exemplu: poți salva userId-ul sau redirecționa
      // localStorage.setItem('userId', data.userId);
      // navigate('/dashboard');

    } catch (error) {
      setMessage(`Login failed: ${error.message}`);
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="User Name"
          value={userName}
          required
          onChange={(e) => setUserName(e.target.value)}
          style={{ width: '100%', marginBottom: '10px', padding: '8px' }}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          required
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: '100%', marginBottom: '10px', padding: '8px' }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          required
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: '100%', marginBottom: '10px', padding: '8px' }}
        />
        <button type="submit" style={{ width: '100%', padding: '10px' }}>
          Login
        </button>
      </form>
      {message && <p style={{ marginTop: '10px' }}>{message}</p>}
    </div>
  );
}

export default LoginForm;
