import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';
import axios from 'axios';

const Profile = ({ user }) => {
  const [profileData, setProfileData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (user?.userName) {
      axios
        .get(`https://localhost:7157/api/Users/user/by-name/${user.userName}`)
        .then(res => setProfileData(res.data))
        .catch(err => {
          console.error('Eroare API:', err);
          alert('Nu s-au putut încărca datele profilului.');
        });
    }
  }, [user]);

  if (!profileData) {
    return <div className="profile-loading">Loading profile...</div>;
  }

  return (
    <div className="profile-container">
      <h2 className="profile-header">Profilul Utilizatorului</h2>
      <div className="profile-info">
        <div className="profile-field"><strong>Utilizator:</strong> {profileData.userName}</div>
        <div className="profile-field"><strong>Cont:</strong> {profileData.accountId}</div>
        <div className="profile-field"><strong>Tip Cont:</strong> {profileData.accountType}</div>
        <div className="profile-field"><strong>Monedă:</strong> {profileData.currency}</div>
      </div>
      <button className="profile-button" onClick={() => navigate('/dashboard')}>
        Înapoi la Dashboard
      </button>
    </div>
  );
};

export default Profile;
